      <h1>
        <strong>{beerData.name}</strong>
      </h1>
      <h2>BREWERS TIPS</h2>
      <p>{beerData.brewers_tips}</p>
      <h2>FOOD PAIRING</h2>
      <p>{beerData.food_pairing}</p>
    </div>
  );
};

export default BeerCard;
