import React, { useState, useEffect } from "react";
import BeerPic from "./BeerPic.js";
import BeerCard from "./BeerCard.js";

const BeerList = () => {
  const [nextPage, setNextPage] = useState(1);
  const [beers, setBeers] = useState(
    fetch(
      "https://api.punkapi.com/v2/beers?page=1&per_page=24"
    ).then((response) => [response.json()])
  );
  const [isLoaded, setIsLoaded] = useState(false);

  const showMoreBeers = () => {
    fetch(`https://api.punkapi.com/v2/beers?page=${nextPage}&per_page=24`)
      .then((response) => response.json())
      .then((responseData) => {
        setBeers(responseData);
      })
      .catch((error) => {
        console.log("Error fetching and parsing data", error);
      });
    setNextPage((n) => n + 1);
  };

  console.log("WTF __OKPOJO", beers && beers, typeof beers);

  return (
    <div>
      <ul className="beer-list"></ul>
      <button onClick={() => showMoreBeers()}>ShowMore</button>
    </div>
  );
};

export default BeerList;
