import React, { useState, useEffect } from "react";
import BeerPic from "./BeerPic.js";

const BeerList = () => {
  const [nextPage, setNextPage] = useState(1);
  const [beers, setBeers] = useState(
    fetch("https://api.punkapi.com/v2/beers/1").then((response) =>
      response.json()
    )
  );

  useEffect(() => {
    fetch(`https://api.punkapi.com/v2/beers?page=${nextPage}&per_page=24`)
      .then((response) => response.json())
      .then((responseData) => {
        setBeers(responseData);
      })
      .catch((error) => {
        console.log("Error fetching and parsing data", error);
      });
    setNextPage(nextPage + 1);
  }, []);

  const showMoreBeers = () => {
    fetch(`https://api.punkapi.com/v2/beers?page=${nextPage}&per_page=24`)
      .then((response) => response.json())
      .then((responseData) => {
        const newBeers = beers.concat(responseData);
        setBeers({ newBeers });
      })
      .catch((error) => {
        console.log("Error fetching and parsing data", error);
      });
  };

  console.log("WTF __OKPOJO", beers && beers, beers.concat(responseData));

  if (beers.length > 0) {
    return (
      <div>
        <ul className="beer-list">
          {beers.map((beer) => {
            return (
              <BeerPic url={beer.image_url} key={beer.id} name={beer.name} />
            );
          })}
        </ul>
        <button onClick={() => showMoreBeers()}>ShowMore</button>
      </div>
    );
  } else {
    return <p>loading...</p>;
  }
};

export default BeerList;
