import React, { useState, useEffect } from "react";
import BeerPic from "./BeerPic.js";

const BeerList = () => {
  const [beers, setBeers] = useState(
    fetch("https://api.punkapi.com/v2/beers?page=1&per_page=24")
      .then((response) => response.json())
      .catch((error) => {
        console.log("Error fetching and parsing data", error);
      })
  );

  //   useEffect(() => {
  // fetch("https://api.punkapi.com/v2/beers?page=1&per_page=24")
  //     .then((response) => response.json())
  //     .then((responseData) => {
  //         setBeers(responseData);
  //     })
  //     .catch((error) => {
  //         console.log("Error fetching and parsing data", error);
  //     })
  //     , []);

  const showMoreBeers = () => {
    fetch("https://api.punkapi.com/v2/beers?page=2&per_page=24")
      .then((response) => response.json())
      .then((responseData) => {
        setBeers(...beers, responseData);
      })
      .catch((error) => {
        console.log("Error fetching and parsing data", error);
      });
  };

  return (
    <div>
      <ul className="beer-list">
        {beers.map((beer) => {
          return (
            <BeerPic url={beer.image_url} key={beer.id} name={beer.name} />
          );
        })}
      </ul>
      {/* <button onClick={() => showMoreBeers()}>ShowMore</button> */}
    </div>
  );
};

export default BeerList;
