import React, { useState, useEffect } from "react";
import qs from "qs";
import BeerPic from "./BeerPic.js";

const BeerList = ({ history, location }) => {
  const [beers, setBeers] = useState([]);
  const { page } = qs.parse(location.search, { ignoreQueryPrefix: true });
  console.log(page);
  const handleClick = (e, id, name) => {
    e.preventDefault();
    let beerId = id;
    let beerName = name;
    console.log(beerId, beerName);
    let path = `beers/${beerId}/${beerName}`;
    console.log(history);
    history.push(path);
  };

  useEffect(() => {
    fetch(`https://api.punkapi.com/v2/beers${page}&per_page=24`)
      .then((response) => response.json())
      .then((responseData) => {
        setBeers(responseData);
      })
      .catch((error) => {
        console.log("Error fetching and parsing data", error);
      });
  }, [page]);

  console.log("Type of BEERS", beers && beers, typeof beers);
  console.log("BEERLIST", page);

  return (
    <div>
      {beers.length > 0 ? (
        <ul className="beer-list">
          {beers.map((beer) => (
            <BeerPic key={beer.id} beer={beer} handleClick={handleClick} />
          ))}
        </ul>
      ) : (
        <p>LOADING...</p>
      )}
      {/* <button onClick={() => setNextPage((prevPage) => prevPage + 1)}>
        ShowMore
      </button> */}
    </div>
  );
};

export default BeerList;
