import React, { useState, useEffect } from "react";
import qs from "qs";
import BeerPic from "./BeerItem.js";

const BeerList = ({ location, ...other }) => {
  const [beers, setBeers] = useState([]);
  console.log(other);

  const queryString = qs.parse(location.search, { ignoreQueryPrefix: true });

  const page = queryString.page || 1;

  useEffect(() => {
    fetch(`https://api.punkapi.com/v2/beers?page=${page}&per_page=24`)
      .then((response) => response.json())
      .then((responseData) => {
        setBeers(responseData);
      })
      .catch((error) => {
        console.log("Error fetching and parsing data", error);
      });
  }, [page]);

  console.log("Type of BEERS", beers && beers, typeof beers);
  console.log("BEERLIST", page);

  return (
    <div>
      <div className="nav-options">
        <input type="select"></input>
      </div>
      {beers.length > 0 ? (
        <ul className="beer-list">
          {beers.map((beer) => (
            <BeerPic key={beer.id} beer={beer} />
          ))}
        </ul>
      ) : (
        <p>LOADING...</p>
      )}
      {/* <button onClick={() => setNextPage((prevPage) => prevPage + 1)}>
        ShowMore
      </button> */}
    </div>
  );
};

export default BeerList;
