import React, { useState, useEffect } from "react";
import qs from "qs";
import BeerPic from "./BeerItem.js";

const BeerList = ({ location }) => {
  const [beers, setBeers] = useState([]);

  const queryString = qs.parse(location.search, { ignoreQueryPrefix: true });
  const page = queryString.page || 1;

  const [itemsPage, setItemsPage] = useState(24);

  useEffect(() => {
    fetch(`https://api.punkapi.com/v2/beers?page=${page}&per_page=${itemsPage}`)
      .then((response) => response.json())
      .then((responseData) => {
        setBeers(responseData);
      })
      .catch((error) => {
        console.log("Error fetching and parsing data", error);
      });
  }, [page, itemsPage]);

  console.log("Type of BEERS", beers && beers, typeof beers);
  console.log("BEERLIST", page);

  return (
    <div>
      <div className="nav-options">
        <button className="page-nav">⇦</button>
        <label>Bières par page :</label>
        <select onChange={(e) => setItemsPage(e.target.value)}>
          <option value={24}>24</option>
          <option value={48}>48</option>
          <option value={72}>72</option>
        </select>
        <button className="page-nav">⇨</button>
      </div>
      {beers.length > 0 ? (
        <ul className="beer-list">
          {beers.map((beer) => (
            <BeerPic key={beer.id} beer={beer} />
          ))}
        </ul>
      ) : (
        <p>LOADING...</p>
      )}
      {/* <button onClick={() => setNextPage((prevPage) => prevPage + 1)}>
        ShowMore
      </button> */}
    </div>
  );
};

export default BeerList;
